import React, { useState, useEffect } from "react";
import { useNavigate } from 'react-router-dom';
import { Box } from "@mui/material";
import Header from "./Header";
import Drawer from "./Drawer";
import SearchSection from "./SearchSection";
import FilterSection from "./FilterSection";
import UserList from "./UserList";
import MapView from "../../MapView";
import {
    fetchUsersByName,
    searchUsersByTopic,
    searchUsersByLocation,
    searchUsersByName
} from "../../api";

const PeopleContainer = () => {
    const [nameQuery, setNameQuery] = useState("");
    const [locationQuery, setLocationQuery] = useState("");
    const [topicQuery, setTopicQuery] = useState("");
    const [filteredData, setFilteredData] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [showList, setShowList] = useState(false);
    const [showMap, setShowMap] = useState(false);
    const [filterOption, setFilterOption] = useState("all");
    const [drawerOpen, setDrawerOpen] = useState(false);
    const [userData, setUserData] = useState([]);

    const navigate = useNavigate();

    useEffect(() => {

    }, [nameQuery, locationQuery, topicQuery, filterOption]);

    const search = (data) => {
        // ... (search logic remains the same)
    };

    const handleSearch = (value, type) => {
        switch (type) {
            case "name":
                // const jsonData = 
                searchUsersByName(value).then(data => {
                    if (data && data.length > 0) {
                        setFilteredData(data.map(i => i.name));
                        setShowSuggestions(true);
                    } else {
                        setShowSuggestions(false);
                    }
                })
                break;
            case "location":
                // setLocationQuery(nameQuery);
                break;
            case "topic":
            // setTopicQuery(nameQuery);
        }
        switch (type) {
            case "name":
                setNameQuery(value);
                break;
            case "location":
                setLocationQuery(value);
                break;
            case "topic":
                setTopicQuery(value);
                break;
            default:
                break;
        }
        setShowSuggestions(true)
    }


    const selectSuggestion = (suggestion, type) => {
        switch (type) {
            case "name":
                fetchUsersByName(suggestion).then(data => {
                    console.log(data)
                    setUserData(data)
                })
                break;
            case "location":
                break;
            case "topic":
                break;
            default:
                console.log("Invalid type")
        }
        setShowList(true)
        setShowSuggestions(false)
        if (type === "name") {
            setNameQuery(suggestion);
        } else if (type === "location") {
            setLocationQuery(suggestion);
        } else if (type === "topic") {
            setTopicQuery(suggestion);
        }
    };

    return (
        <Box sx={{ p: 2 }}>
            <Header setDrawerOpen={setDrawerOpen} />
            <Drawer
                drawerOpen={drawerOpen}
                setDrawerOpen={setDrawerOpen}
                navigate={navigate}
                showMap={showMap}
                setShowMap={setShowMap}
            />
            <Box sx={{ marginTop: '64px' }}>
                {showMap ? (
                    <MapView userData={filteredData} />
                ) : (
                    <>
                        <SearchSection
                            nameQuery={nameQuery}
                            locationQuery={locationQuery}
                            topicQuery={topicQuery}
                            handleSearch={handleSearch}
                            showSuggestions={showSuggestions}
                            selectSuggestion={selectSuggestion}
                            filterData={filteredData}
                        />
                        <FilterSection
                            filterOption={filterOption}
                            setFilterOption={setFilterOption}
                        />
                        {showList && (
                            <UserList userData={userData} />
                        )}
                    </>
                )}
            </Box>
        </Box>
    );
};


export default PeopleContainer;
